import{_ as i,c as n,o as l,ag as e}from"./chunks/framework.jxTED6Pp.js";const m=JSON.parse('{"title":"Laporan Keuangan - Tambah Laporan","description":"","frontmatter":{},"headers":[],"relativePath":"features/add-report.md","filePath":"features/add-report.md"}'),r={name:"features/add-report.md"};function t(o,a,s,u,d,h){return l(),n("div",null,[...a[0]||(a[0]=[e('<h1 id="laporan-keuangan-tambah-laporan" tabindex="-1">Laporan Keuangan - Tambah Laporan <a class="header-anchor" href="#laporan-keuangan-tambah-laporan" aria-label="Permalink to &quot;Laporan Keuangan - Tambah Laporan&quot;">​</a></h1><p>Panduan untuk menambahkan laporan keuangan baru.</p><h2 id="akses-halaman" tabindex="-1">Akses Halaman <a class="header-anchor" href="#akses-halaman" aria-label="Permalink to &quot;Akses Halaman&quot;">​</a></h2><ul><li>Dari halaman daftar laporan, klik tombol <strong>Tambah Laporan</strong></li><li>Atau akses langsung: <code>/reports/new</code></li></ul><h2 id="form-tambah-laporan" tabindex="-1">Form Tambah Laporan <a class="header-anchor" href="#form-tambah-laporan" aria-label="Permalink to &quot;Form Tambah Laporan&quot;">​</a></h2><h3 id="informasi-dasar" tabindex="-1">Informasi Dasar <a class="header-anchor" href="#informasi-dasar" aria-label="Permalink to &quot;Informasi Dasar&quot;">​</a></h3><p><strong>Field Wajib:</strong></p><ul><li><strong>Periode</strong> *: Pilih periode laporan (format: YYYY-MM)</li><li><strong>Perusahaan</strong> *: Pilih perusahaan</li><li><strong>Revenue</strong> *: Total pendapatan</li><li><strong>OPEX</strong> *: Total biaya operasional</li><li><strong>NPAT</strong> *: Net Profit After Tax</li><li><strong>Dividend</strong> *: Dividen</li><li><strong>Financial Ratio</strong> *: Rasio keuangan (Revenue/OPEX)</li></ul><p><strong>Field Opsional:</strong></p><ul><li><strong>Inputter</strong>: User yang menginput (default: Anda)</li><li><strong>Remark</strong>: Catatan tambahan</li><li><strong>Attachment</strong>: File pendukung (Excel, PDF, dll)</li></ul><h3 id="validasi-otomatis" tabindex="-1">Validasi Otomatis <a class="header-anchor" href="#validasi-otomatis" aria-label="Permalink to &quot;Validasi Otomatis&quot;">​</a></h3><p>Sistem akan memvalidasi:</p><ul><li>Periode harus format YYYY-MM (contoh: 2025-01)</li><li>Tidak boleh ada duplicate periode untuk perusahaan yang sama</li><li>Financial Ratio akan terhitung otomatis (Revenue/OPEX)</li><li>Company ID harus valid</li><li>Inputter ID harus valid (jika diisi)</li></ul><h3 id="perhitungan-otomatis" tabindex="-1">Perhitungan Otomatis <a class="header-anchor" href="#perhitungan-otomatis" aria-label="Permalink to &quot;Perhitungan Otomatis&quot;">​</a></h3><ul><li><strong>Financial Ratio</strong>: Otomatis terhitung = Revenue / OPEX</li><li>Sistem akan menampilkan perhitungan di form</li></ul><h2 id="menyimpan-laporan" tabindex="-1">Menyimpan Laporan <a class="header-anchor" href="#menyimpan-laporan" aria-label="Permalink to &quot;Menyimpan Laporan&quot;">​</a></h2><ol><li><p><strong>Isi Form</strong></p><ul><li>Isi semua field wajib</li><li>Pastikan data sudah benar</li></ul></li><li><p><strong>Review</strong></p><ul><li>Cek perhitungan financial ratio</li><li>Pastikan periode benar</li></ul></li><li><p><strong>Klik &quot;Simpan&quot;</strong></p><ul><li>Sistem akan menyimpan laporan</li><li>Jika berhasil, Anda akan diarahkan ke halaman daftar laporan</li></ul></li><li><p><strong>Jika Ada Error</strong></p><ul><li>Sistem akan menampilkan pesan error</li><li>Perbaiki field yang error</li><li>Coba simpan lagi</li></ul></li></ol><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;Tips&quot;">​</a></h2><ul><li>Pastikan periode benar sebelum simpan</li><li>Cek apakah laporan untuk periode tersebut sudah ada</li><li>Gunakan remark untuk catatan penting</li><li>Upload attachment jika ada file pendukung</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="periode-sudah-ada" tabindex="-1">Periode Sudah Ada <a class="header-anchor" href="#periode-sudah-ada" aria-label="Permalink to &quot;Periode Sudah Ada&quot;">​</a></h3><ul><li>Tidak boleh ada duplicate periode untuk perusahaan yang sama</li><li>Jika perlu update, gunakan fitur edit</li><li>Atau hapus laporan lama terlebih dahulu</li></ul><h3 id="financial-ratio-tidak-valid" tabindex="-1">Financial Ratio Tidak Valid <a class="header-anchor" href="#financial-ratio-tidak-valid" aria-label="Permalink to &quot;Financial Ratio Tidak Valid&quot;">​</a></h3><ul><li>Pastikan OPEX tidak 0 (akan menyebabkan error division by zero)</li><li>Sistem akan menghitung otomatis, pastikan Revenue dan OPEX benar</li></ul><h3 id="perusahaan-tidak-muncul-di-dropdown" tabindex="-1">Perusahaan Tidak Muncul di Dropdown <a class="header-anchor" href="#perusahaan-tidak-muncul-di-dropdown" aria-label="Permalink to &quot;Perusahaan Tidak Muncul di Dropdown&quot;">​</a></h3><ul><li>Pastikan Anda memiliki akses ke perusahaan tersebut</li><li>Hubungi administrator jika perusahaan seharusnya muncul</li></ul><h2 id="langkah-selanjutnya" tabindex="-1">Langkah Selanjutnya <a class="header-anchor" href="#langkah-selanjutnya" aria-label="Permalink to &quot;Langkah Selanjutnya&quot;">​</a></h2><ul><li><a href="./edit-report.html">Edit Laporan</a> - Pelajari cara edit laporan</li><li><a href="./bulk-upload-reports.html">Bulk Upload</a> - Pelajari bulk upload untuk input banyak laporan</li></ul>',28)])])}const k=i(r,[["render",t]]);export{m as __pageData,k as default};
