import{_ as n,c as i,o as e,ag as l}from"./chunks/framework.jxTED6Pp.js";const m=JSON.parse('{"title":"Manajemen Pengguna - Tambah Pengguna","description":"","frontmatter":{},"headers":[],"relativePath":"features/add-user.md","filePath":"features/add-user.md"}'),s={name:"features/add-user.md"};function r(t,a,u,o,d,h){return e(),i("div",null,[...a[0]||(a[0]=[l('<h1 id="manajemen-pengguna-tambah-pengguna" tabindex="-1">Manajemen Pengguna - Tambah Pengguna <a class="header-anchor" href="#manajemen-pengguna-tambah-pengguna" aria-label="Permalink to &quot;Manajemen Pengguna - Tambah Pengguna&quot;">​</a></h1><p>Panduan untuk menambahkan pengguna baru ke dalam sistem.</p><h2 id="akses-halaman" tabindex="-1">Akses Halaman <a class="header-anchor" href="#akses-halaman" aria-label="Permalink to &quot;Akses Halaman&quot;">​</a></h2><ul><li>Dari halaman daftar pengguna, klik tombol <strong>Tambah Pengguna</strong></li><li>Atau akses langsung: <code>/users/new</code></li><li><strong>Catatan</strong>: Hanya admin/administrator yang dapat akses</li></ul><h2 id="form-tambah-pengguna" tabindex="-1">Form Tambah Pengguna <a class="header-anchor" href="#form-tambah-pengguna" aria-label="Permalink to &quot;Form Tambah Pengguna&quot;">​</a></h2><h3 id="informasi-dasar" tabindex="-1">Informasi Dasar <a class="header-anchor" href="#informasi-dasar" aria-label="Permalink to &quot;Informasi Dasar&quot;">​</a></h3><p><strong>Field Wajib:</strong></p><ul><li><strong>Username</strong> *: Username untuk login (harus unik)</li><li><strong>Email</strong> *: Email pengguna (harus unik)</li><li><strong>Password</strong> *: Password untuk login</li><li><strong>Role</strong> *: Pilih peran pengguna</li></ul><p><strong>Field Opsional:</strong></p><ul><li><strong>Nama Lengkap</strong>: Nama lengkap pengguna</li><li><strong>Telepon</strong>: Nomor telepon</li><li><strong>Status</strong>: Aktif atau Nonaktif (default: Aktif)</li></ul><h3 id="assignment-perusahaan" tabindex="-1">Assignment Perusahaan <a class="header-anchor" href="#assignment-perusahaan" aria-label="Permalink to &quot;Assignment Perusahaan&quot;">​</a></h3><p><strong>Menambah Assignment:</strong></p><ol><li><p><strong>Klik &quot;Tambah Assignment&quot;</strong></p></li><li><p><strong>Pilih Perusahaan</strong></p><ul><li>Pilih perusahaan dari dropdown</li><li>Bisa pilih beberapa perusahaan</li></ul></li><li><p><strong>Pilih Role di Perusahaan</strong></p><ul><li>Setiap perusahaan bisa memiliki role berbeda</li><li>Contoh: Admin di Perusahaan A, Manager di Perusahaan B</li></ul></li><li><p><strong>Simpan Assignment</strong></p><ul><li>Assignment akan ditambahkan ke list</li><li>Bisa menambah multiple assignments</li></ul></li></ol><p><strong>Menghapus Assignment:</strong></p><ul><li>Klik tombol hapus (X) di assignment yang ingin dihapus</li></ul><h3 id="standby-user" tabindex="-1">Standby User <a class="header-anchor" href="#standby-user" aria-label="Permalink to &quot;Standby User&quot;">​</a></h3><p>Pengguna bisa dibuat tanpa assignment perusahaan (standby):</p><ul><li>User akan dibuat tanpa assignment</li><li>Assignment bisa ditambahkan nanti</li><li>Berguna untuk user yang belum ditentukan perusahaannya</li></ul><h2 id="validasi" tabindex="-1">Validasi <a class="header-anchor" href="#validasi" aria-label="Permalink to &quot;Validasi&quot;">​</a></h2><p>Sistem akan memvalidasi:</p><ul><li>Username harus unik</li><li>Email harus unik dan format valid</li><li>Password harus memenuhi kriteria (minimal 8 karakter)</li><li>Role harus valid</li><li>Company assignment harus valid (jika diisi)</li></ul><h2 id="menyimpan-pengguna" tabindex="-1">Menyimpan Pengguna <a class="header-anchor" href="#menyimpan-pengguna" aria-label="Permalink to &quot;Menyimpan Pengguna&quot;">​</a></h2><ol><li><p><strong>Isi Form</strong></p><ul><li>Isi semua field wajib</li><li>Tambah assignment perusahaan jika diperlukan</li></ul></li><li><p><strong>Review</strong></p><ul><li>Pastikan username dan email benar</li><li>Cek assignment perusahaan</li></ul></li><li><p><strong>Klik &quot;Simpan&quot;</strong></p><ul><li>Sistem akan menyimpan pengguna</li><li>Jika berhasil, Anda akan diarahkan ke halaman daftar pengguna</li></ul></li><li><p><strong>Jika Ada Error</strong></p><ul><li>Sistem akan menampilkan pesan error</li><li>Perbaiki field yang error</li><li>Coba simpan lagi</li></ul></li></ol><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;Tips&quot;">​</a></h2><ul><li>Gunakan username yang mudah diingat</li><li>Pastikan email valid dan aktif</li><li>Buat password yang kuat (minimal 8 karakter, kombinasi huruf dan angka)</li><li>Assign ke perusahaan yang sesuai dengan role</li><li>Buat standby user jika perusahaan belum ditentukan</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="username-sudah-ada" tabindex="-1">Username Sudah Ada <a class="header-anchor" href="#username-sudah-ada" aria-label="Permalink to &quot;Username Sudah Ada&quot;">​</a></h3><ul><li>Username harus unik</li><li>Gunakan username yang berbeda</li><li>Cek daftar pengguna untuk melihat username yang sudah digunakan</li></ul><h3 id="email-sudah-ada" tabindex="-1">Email Sudah Ada <a class="header-anchor" href="#email-sudah-ada" aria-label="Permalink to &quot;Email Sudah Ada&quot;">​</a></h3><ul><li>Email harus unik</li><li>Gunakan email yang berbeda</li><li>Cek apakah email sudah digunakan pengguna lain</li></ul><h3 id="password-tidak-valid" tabindex="-1">Password Tidak Valid <a class="header-anchor" href="#password-tidak-valid" aria-label="Permalink to &quot;Password Tidak Valid&quot;">​</a></h3><ul><li>Password minimal 8 karakter</li><li>Disarankan kombinasi huruf besar, huruf kecil, dan angka</li><li>Pastikan password tidak terlalu mudah ditebak</li></ul><h3 id="perusahaan-tidak-muncul-di-dropdown" tabindex="-1">Perusahaan Tidak Muncul di Dropdown <a class="header-anchor" href="#perusahaan-tidak-muncul-di-dropdown" aria-label="Permalink to &quot;Perusahaan Tidak Muncul di Dropdown&quot;">​</a></h3><ul><li>Pastikan perusahaan sudah ada di sistem</li><li>Pastikan perusahaan status aktif</li><li>Hubungi administrator jika perusahaan seharusnya muncul</li></ul><h2 id="langkah-selanjutnya" tabindex="-1">Langkah Selanjutnya <a class="header-anchor" href="#langkah-selanjutnya" aria-label="Permalink to &quot;Langkah Selanjutnya&quot;">​</a></h2><ul><li><a href="./edit-user.html">Edit Pengguna</a> - Pelajari cara edit pengguna</li><li><a href="./user-management.html">Daftar Pengguna</a> - Kembali ke daftar pengguna</li></ul>',36)])])}const p=n(s,[["render",r]]);export{m as __pageData,p as default};
