import{_ as e,c as i,o as l,ag as n}from"./chunks/framework.jxTED6Pp.js";const h=JSON.parse('{"title":"Manajemen Dokumen - Detail Dokumen","description":"","frontmatter":{},"headers":[],"relativePath":"features/document-detail.md","filePath":"features/document-detail.md"}'),t={name:"features/document-detail.md"};function o(r,a,d,u,k,m){return l(),i("div",null,[...a[0]||(a[0]=[n('<h1 id="manajemen-dokumen-detail-dokumen" tabindex="-1">Manajemen Dokumen - Detail Dokumen <a class="header-anchor" href="#manajemen-dokumen-detail-dokumen" aria-label="Permalink to &quot;Manajemen Dokumen - Detail Dokumen&quot;">​</a></h1><p>Halaman detail menampilkan informasi lengkap tentang dokumen.</p><h2 id="akses-halaman" tabindex="-1">Akses Halaman <a class="header-anchor" href="#akses-halaman" aria-label="Permalink to &quot;Akses Halaman&quot;">​</a></h2><ul><li>Klik nama dokumen di daftar dokumen</li><li>Atau akses langsung: <code>/documents/{id}</code></li></ul><h2 id="tampilan-detail" tabindex="-1">Tampilan Detail <a class="header-anchor" href="#tampilan-detail" aria-label="Permalink to &quot;Tampilan Detail&quot;">​</a></h2><p>Halaman menampilkan:</p><h3 id="informasi-dokumen" tabindex="-1">Informasi Dokumen <a class="header-anchor" href="#informasi-dokumen" aria-label="Permalink to &quot;Informasi Dokumen&quot;">​</a></h3><ul><li><strong>Judul</strong>: Judul dokumen</li><li><strong>Nomor Referensi</strong>: Nomor referensi</li><li><strong>Tipe Dokumen</strong>: Tipe dokumen</li><li><strong>Folder</strong>: Folder tempat dokumen</li><li><strong>Status</strong>: Status dokumen (Aktif/Nonaktif)</li><li><strong>File</strong>: Nama file dan ukuran</li></ul><h3 id="metadata" tabindex="-1">Metadata <a class="header-anchor" href="#metadata" aria-label="Permalink to &quot;Metadata&quot;">​</a></h3><ul><li><strong>Unit</strong>: Unit atau departemen</li><li><strong>Tanggal Terbit</strong>: Tanggal dokumen diterbitkan</li><li><strong>Tanggal Efektif</strong>: Tanggal dokumen mulai efektif</li><li><strong>Tanggal Kedaluwarsa</strong>: Tanggal dokumen kedaluwarsa</li><li><strong>Status Aktif</strong>: Status aktif dokumen</li><li><strong>Uploader</strong>: User yang mengupload</li><li><strong>Tanggal Upload</strong>: Tanggal dokumen diupload</li><li><strong>Tanggal Update</strong>: Tanggal terakhir diupdate</li></ul><h3 id="preview-dokumen" tabindex="-1">Preview Dokumen <a class="header-anchor" href="#preview-dokumen" aria-label="Permalink to &quot;Preview Dokumen&quot;">​</a></h3><ul><li><strong>Preview Area</strong>: Menampilkan preview dokumen</li><li><strong>Download</strong>: Tombol untuk download dokumen</li></ul><h2 id="fitur" tabindex="-1">Fitur <a class="header-anchor" href="#fitur" aria-label="Permalink to &quot;Fitur&quot;">​</a></h2><h3 id="preview-dokumen-1" tabindex="-1">Preview Dokumen <a class="header-anchor" href="#preview-dokumen-1" aria-label="Permalink to &quot;Preview Dokumen&quot;">​</a></h3><p><strong>Untuk Gambar (JPG, PNG):</strong></p><ul><li>Preview langsung ditampilkan di halaman</li><li>Klik gambar untuk preview fullscreen</li><li>Zoom in/out dengan mouse wheel</li></ul><p><strong>Untuk PDF:</strong></p><ul><li>Preview PDF ditampilkan di halaman</li><li>Klik preview untuk fullscreen</li><li>Navigasi halaman dengan tombol atau scroll</li></ul><p><strong>Untuk Dokumen Lain (DOCX, XLSX, dll):</strong></p><ul><li>Tidak ada preview visual</li><li>Hanya tombol download yang tersedia</li></ul><h3 id="download-dokumen" tabindex="-1">Download Dokumen <a class="header-anchor" href="#download-dokumen" aria-label="Permalink to &quot;Download Dokumen&quot;">​</a></h3><ol><li>Klik tombol <strong>Download</strong></li><li>File akan terdownload ke komputer Anda</li><li>File akan menggunakan nama asli saat upload</li></ol><h3 id="edit-dokumen" tabindex="-1">Edit Dokumen <a class="header-anchor" href="#edit-dokumen" aria-label="Permalink to &quot;Edit Dokumen&quot;">​</a></h3><ol><li>Klik tombol <strong>Edit</strong> di pojok kanan atas</li><li>Anda akan diarahkan ke halaman edit</li><li><strong>Catatan</strong>: Hanya dapat edit metadata, tidak dapat edit file</li></ol><h3 id="delete-dokumen" tabindex="-1">Delete Dokumen <a class="header-anchor" href="#delete-dokumen" aria-label="Permalink to &quot;Delete Dokumen&quot;">​</a></h3><ol><li>Klik tombol <strong>Delete</strong> di pojok kanan atas</li><li>Konfirmasi penghapusan</li><li><strong>Catatan</strong>: Hanya administrator yang dapat delete</li></ol><h3 id="lengkapi-metadata" tabindex="-1">Lengkapi Metadata <a class="header-anchor" href="#lengkapi-metadata" aria-label="Permalink to &quot;Lengkapi Metadata&quot;">​</a></h3><p>Jika metadata belum lengkap:</p><ol><li>Klik tombol <strong>Lengkapi Meta Data</strong></li><li>Anda akan diarahkan ke halaman edit</li><li>Isi metadata yang belum lengkap</li><li>Simpan perubahan</li></ol><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;Tips&quot;">​</a></h2><ul><li>Gunakan preview untuk melihat dokumen sebelum download</li><li>Lengkapi metadata untuk memudahkan pencarian</li><li>Cek tanggal kedaluwarsa untuk tracking</li><li>Download dokumen penting sebagai backup</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="preview-tidak-tampil" tabindex="-1">Preview Tidak Tampil <a class="header-anchor" href="#preview-tidak-tampil" aria-label="Permalink to &quot;Preview Tidak Tampil&quot;">​</a></h3><ul><li>Pastikan file adalah gambar (JPG, PNG) atau PDF</li><li>File DOCX/XLSX tidak bisa di-preview, hanya download</li><li>Cek koneksi internet jika preview tidak muncul</li><li>Coba refresh halaman</li></ul><h3 id="tidak-bisa-download" tabindex="-1">Tidak Bisa Download <a class="header-anchor" href="#tidak-bisa-download" aria-label="Permalink to &quot;Tidak Bisa Download&quot;">​</a></h3><ul><li>Cek koneksi internet</li><li>Coba browser lain</li><li>Hubungi administrator jika masalah berlanjut</li></ul><h3 id="metadata-tidak-lengkap" tabindex="-1">Metadata Tidak Lengkap <a class="header-anchor" href="#metadata-tidak-lengkap" aria-label="Permalink to &quot;Metadata Tidak Lengkap&quot;">​</a></h3><ul><li>Klik &quot;Lengkapi Meta Data&quot; untuk melengkapi</li><li>Isi semua field yang diperlukan</li><li>Simpan perubahan</li></ul><h2 id="langkah-selanjutnya" tabindex="-1">Langkah Selanjutnya <a class="header-anchor" href="#langkah-selanjutnya" aria-label="Permalink to &quot;Langkah Selanjutnya&quot;">​</a></h2><ul><li><a href="./upload-document.html">Upload Dokumen</a> - Upload dokumen baru</li><li><a href="./document-management.html">Daftar Dokumen</a> - Kembali ke daftar dokumen</li></ul>',40)])])}const g=e(t,[["render",o]]);export{h as __pageData,g as default};
