import{_ as e,c as l,o as i,ag as n}from"./chunks/framework.jxTED6Pp.js";const m=JSON.parse('{"title":"Manajemen Dokumen - Folder & Kategori","description":"","frontmatter":{},"headers":[],"relativePath":"features/document-folders.md","filePath":"features/document-folders.md"}'),o={name:"features/document-folders.md"};function r(t,a,d,s,u,h){return i(),l("div",null,[...a[0]||(a[0]=[n(`<h1 id="manajemen-dokumen-folder-kategori" tabindex="-1">Manajemen Dokumen - Folder &amp; Kategori <a class="header-anchor" href="#manajemen-dokumen-folder-kategori" aria-label="Permalink to &quot;Manajemen Dokumen - Folder &amp; Kategori&quot;">​</a></h1><p>Panduan untuk mengorganisir dokumen menggunakan folder.</p><h2 id="konsep-folder" tabindex="-1">Konsep Folder <a class="header-anchor" href="#konsep-folder" aria-label="Permalink to &quot;Konsep Folder&quot;">​</a></h2><p>Folder digunakan untuk mengorganisir dokumen dalam kategori yang logis:</p><ul><li>Setiap dokumen harus berada di dalam folder</li><li>Folder dapat memiliki subfolder (hierarki)</li><li>Folder membantu pencarian dan navigasi</li></ul><h2 id="membuat-folder" tabindex="-1">Membuat Folder <a class="header-anchor" href="#membuat-folder" aria-label="Permalink to &quot;Membuat Folder&quot;">​</a></h2><h3 id="folder-baru" tabindex="-1">Folder Baru <a class="header-anchor" href="#folder-baru" aria-label="Permalink to &quot;Folder Baru&quot;">​</a></h3><ol><li><p><strong>Dari Halaman Dokumen</strong></p><ul><li>Klik tombol <strong>Add Folder</strong> atau **Add new folder&quot;</li><li>Masukkan nama folder</li><li>Klik <strong>Buat</strong></li></ul></li><li><p><strong>Dari Halaman Folder Detail</strong></p><ul><li>Klik tombol <strong>Add Subfolder</strong></li><li>Masukkan nama subfolder</li><li>Klik <strong>Buat</strong></li></ul></li></ol><h3 id="subfolder" tabindex="-1">Subfolder <a class="header-anchor" href="#subfolder" aria-label="Permalink to &quot;Subfolder&quot;">​</a></h3><ul><li>Subfolder adalah folder di dalam folder lain</li><li>Membentuk struktur hierarki</li><li>Tidak ada batasan level kedalaman</li></ul><p><strong>Contoh Struktur:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Documents/</span></span>
<span class="line"><span>├── Finance/</span></span>
<span class="line"><span>│   ├── Invoices/</span></span>
<span class="line"><span>│   └── Reports/</span></span>
<span class="line"><span>├── Legal/</span></span>
<span class="line"><span>│   └── Contracts/</span></span>
<span class="line"><span>└── HR/</span></span>
<span class="line"><span>    └── Policies/</span></span></code></pre></div><h2 id="mengelola-folder" tabindex="-1">Mengelola Folder <a class="header-anchor" href="#mengelola-folder" aria-label="Permalink to &quot;Mengelola Folder&quot;">​</a></h2><h3 id="rename-folder" tabindex="-1">Rename Folder <a class="header-anchor" href="#rename-folder" aria-label="Permalink to &quot;Rename Folder&quot;">​</a></h3><ol><li>Klik menu (3 dots) di folder</li><li>Pilih <strong>Rename</strong></li><li>Masukkan nama baru</li><li>Klik <strong>Simpan</strong></li></ol><p><strong>Catatan</strong>: Hanya administrator yang dapat rename</p><h3 id="delete-folder" tabindex="-1">Delete Folder <a class="header-anchor" href="#delete-folder" aria-label="Permalink to &quot;Delete Folder&quot;">​</a></h3><ol><li>Klik menu (3 dots) di folder</li><li>Pilih <strong>Delete</strong></li><li>Konfirmasi penghapusan</li></ol><p><strong>Catatan</strong>:</p><ul><li>Hanya administrator yang dapat delete</li><li>Folder yang berisi dokumen tidak bisa dihapus (pindahkan dokumen dulu)</li></ul><h3 id="membuka-folder" tabindex="-1">Membuka Folder <a class="header-anchor" href="#membuka-folder" aria-label="Permalink to &quot;Membuka Folder&quot;">​</a></h3><ol><li><strong>Single Click</strong>: Pilih folder (highlight)</li><li><strong>Double Click</strong>: Buka folder dan lihat isinya</li><li><strong>Klik Nama Folder</strong>: Buka folder di halaman detail</li></ol><h2 id="upload-ke-folder" tabindex="-1">Upload ke Folder <a class="header-anchor" href="#upload-ke-folder" aria-label="Permalink to &quot;Upload ke Folder&quot;">​</a></h2><h3 id="upload-single-file" tabindex="-1">Upload Single File <a class="header-anchor" href="#upload-single-file" aria-label="Permalink to &quot;Upload Single File&quot;">​</a></h3><ol><li>Buka folder yang diinginkan</li><li>Klik tombol <strong>Upload File</strong></li><li>Pilih folder saat upload</li><li>Upload file</li></ol><h3 id="batch-upload" tabindex="-1">Batch Upload <a class="header-anchor" href="#batch-upload" aria-label="Permalink to &quot;Batch Upload&quot;">​</a></h3><ol><li>Buka folder detail</li><li>Di area upload (label &quot;Multiple files supported&quot;)</li><li>Drag &amp; drop atau browse beberapa file</li><li>Klik <strong>Upload</strong></li><li>Semua file akan tersimpan di folder tersebut</li></ol><h2 id="navigasi-folder" tabindex="-1">Navigasi Folder <a class="header-anchor" href="#navigasi-folder" aria-label="Permalink to &quot;Navigasi Folder&quot;">​</a></h2><h3 id="breadcrumb" tabindex="-1">Breadcrumb <a class="header-anchor" href="#breadcrumb" aria-label="Permalink to &quot;Breadcrumb&quot;">​</a></h3><p>Di halaman folder detail, breadcrumb menampilkan:</p><ul><li>Path folder saat ini</li><li>Klik folder di breadcrumb untuk navigasi cepat</li></ul><p><strong>Contoh:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Documents &gt; Finance &gt; Invoices</span></span></code></pre></div><h3 id="kembali-ke-parent" tabindex="-1">Kembali ke Parent <a class="header-anchor" href="#kembali-ke-parent" aria-label="Permalink to &quot;Kembali ke Parent&quot;">​</a></h3><ul><li>Klik tombol <strong>Back</strong> atau folder parent di breadcrumb</li><li>Atau klik &quot;Documents&quot; untuk kembali ke root</li></ul><h2 id="tips-organisasi" tabindex="-1">Tips Organisasi <a class="header-anchor" href="#tips-organisasi" aria-label="Permalink to &quot;Tips Organisasi&quot;">​</a></h2><h3 id="naming-convention" tabindex="-1">Naming Convention <a class="header-anchor" href="#naming-convention" aria-label="Permalink to &quot;Naming Convention&quot;">​</a></h3><ul><li>Gunakan nama yang deskriptif dan konsisten</li><li>Contoh: &quot;2025-Invoices&quot;, &quot;Q1-Reports&quot;, &quot;Legal-Contracts&quot;</li></ul><h3 id="struktur-hierarki" tabindex="-1">Struktur Hierarki <a class="header-anchor" href="#struktur-hierarki" aria-label="Permalink to &quot;Struktur Hierarki&quot;">​</a></h3><ul><li>Jangan terlalu dalam (maksimal 3-4 level)</li><li>Group berdasarkan kategori utama</li><li>Gunakan subfolder untuk sub-kategori</li></ul><h3 id="best-practices" tabindex="-1">Best Practices <a class="header-anchor" href="#best-practices" aria-label="Permalink to &quot;Best Practices&quot;">​</a></h3><ul><li>Satu folder untuk satu kategori dokumen</li><li>Gunakan folder untuk grouping, bukan untuk setiap dokumen</li><li>Review dan cleanup folder secara berkala</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="tidak-bisa-buat-folder" tabindex="-1">Tidak Bisa Buat Folder <a class="header-anchor" href="#tidak-bisa-buat-folder" aria-label="Permalink to &quot;Tidak Bisa Buat Folder&quot;">​</a></h3><ul><li>Pastikan Anda memiliki akses (admin/administrator)</li><li>Cek apakah nama folder sudah ada</li><li>Hubungi administrator jika masalah berlanjut</li></ul><h3 id="folder-tidak-bisa-dihapus" tabindex="-1">Folder Tidak Bisa Dihapus <a class="header-anchor" href="#folder-tidak-bisa-dihapus" aria-label="Permalink to &quot;Folder Tidak Bisa Dihapus&quot;">​</a></h3><ul><li>Pastikan folder kosong (tidak ada dokumen)</li><li>Pindahkan atau hapus dokumen terlebih dahulu</li><li>Hanya administrator yang dapat delete</li></ul><h3 id="tidak-bisa-rename" tabindex="-1">Tidak Bisa Rename <a class="header-anchor" href="#tidak-bisa-rename" aria-label="Permalink to &quot;Tidak Bisa Rename&quot;">​</a></h3><ul><li>Pastikan Anda memiliki akses (admin/administrator)</li><li>Cek apakah nama baru sudah digunakan folder lain</li><li>Hubungi administrator jika masalah berlanjut</li></ul><h2 id="langkah-selanjutnya" tabindex="-1">Langkah Selanjutnya <a class="header-anchor" href="#langkah-selanjutnya" aria-label="Permalink to &quot;Langkah Selanjutnya&quot;">​</a></h2><ul><li><a href="./document-management.html">Daftar Dokumen</a> - Kembali ke daftar dokumen</li><li><a href="./upload-document.html">Upload Dokumen</a> - Upload dokumen ke folder</li></ul>`,51)])])}const p=e(o,[["render",r]]);export{m as __pageData,p as default};
