import{_ as e,c as l,o as i,ag as n}from"./chunks/framework.jxTED6Pp.js";const k=JSON.parse('{"title":"Manajemen Dokumen - Daftar Dokumen","description":"","frontmatter":{},"headers":[],"relativePath":"features/document-management.md","filePath":"features/document-management.md"}'),t={name:"features/document-management.md"};function r(o,a,d,u,s,m){return i(),l("div",null,[...a[0]||(a[0]=[n('<h1 id="manajemen-dokumen-daftar-dokumen" tabindex="-1">Manajemen Dokumen - Daftar Dokumen <a class="header-anchor" href="#manajemen-dokumen-daftar-dokumen" aria-label="Permalink to &quot;Manajemen Dokumen - Daftar Dokumen&quot;">​</a></h1><p>Halaman ini menampilkan semua dokumen yang dapat Anda akses.</p><h2 id="akses-halaman" tabindex="-1">Akses Halaman <a class="header-anchor" href="#akses-halaman" aria-label="Permalink to &quot;Akses Halaman&quot;">​</a></h2><ul><li>Klik menu <strong>Dokumen</strong> di navigasi atas</li><li>Atau akses langsung: <code>/documents</code></li></ul><h2 id="tampilan" tabindex="-1">Tampilan <a class="header-anchor" href="#tampilan" aria-label="Permalink to &quot;Tampilan&quot;">​</a></h2><p>Halaman terdiri dari beberapa bagian:</p><h3 id="sidebar-kiri" tabindex="-1">Sidebar Kiri <a class="header-anchor" href="#sidebar-kiri" aria-label="Permalink to &quot;Sidebar Kiri&quot;">​</a></h3><ul><li><strong>Search</strong>: Pencarian dokumen</li><li><strong>Folders</strong>: Daftar folder untuk navigasi</li><li><strong>Activity</strong>: Aktivitas terbaru</li><li><strong>Storage</strong>: Informasi penggunaan storage</li></ul><h3 id="area-utama" tabindex="-1">Area Utama <a class="header-anchor" href="#area-utama" aria-label="Permalink to &quot;Area Utama&quot;">​</a></h3><h4 id="bagian-folders" tabindex="-1">Bagian Folders <a class="header-anchor" href="#bagian-folders" aria-label="Permalink to &quot;Bagian Folders&quot;">​</a></h4><p>Menampilkan folder dalam format grid atau list:</p><ul><li><strong>Grid View</strong>: Tampilan kartu folder</li><li><strong>List View</strong>: Tampilan tabel folder</li><li><strong>Add Folder</strong>: Tombol untuk menambah folder baru</li></ul><p><strong>Fitur Folder:</strong></p><ul><li>Klik folder untuk melihat dokumen di dalamnya</li><li>Double-click untuk membuka folder</li><li>Klik menu (3 dots) untuk rename atau delete folder</li></ul><h4 id="bagian-recently-files" tabindex="-1">Bagian Recently Files <a class="header-anchor" href="#bagian-recently-files" aria-label="Permalink to &quot;Bagian Recently Files&quot;">​</a></h4><p>Tabel menampilkan file terbaru:</p><ul><li><strong>Nama File</strong>: Nama dokumen</li><li><strong>Tipe</strong>: Tipe file (PDF, DOCX, dll)</li><li><strong>Folder</strong>: Folder tempat dokumen</li><li><strong>Uploader</strong>: User yang mengupload</li><li><strong>Tanggal</strong>: Tanggal upload/update</li><li><strong>Status Metadata</strong>: Status kelengkapan metadata</li></ul><h2 id="fitur" tabindex="-1">Fitur <a class="header-anchor" href="#fitur" aria-label="Permalink to &quot;Fitur&quot;">​</a></h2><h3 id="pencarian" tabindex="-1">Pencarian <a class="header-anchor" href="#pencarian" aria-label="Permalink to &quot;Pencarian&quot;">​</a></h3><ul><li>Gunakan search box di sidebar untuk mencari dokumen</li><li>Pencarian berdasarkan nama file</li><li>Hasil pencarian ditampilkan di modal</li></ul><h3 id="filter" tabindex="-1">Filter <a class="header-anchor" href="#filter" aria-label="Permalink to &quot;Filter&quot;">​</a></h3><ul><li><strong>Filter Folder</strong>: Pilih folder untuk melihat dokumen di folder tersebut</li><li><strong>Filter Tipe</strong>: Filter berdasarkan tipe file</li><li><strong>Filter Waktu</strong>: All Time, Last Month, Last Week</li></ul><h3 id="view-mode" tabindex="-1">View Mode <a class="header-anchor" href="#view-mode" aria-label="Permalink to &quot;View Mode&quot;">​</a></h3><ul><li><strong>Grid View</strong>: Tampilan kartu (default)</li><li><strong>List View</strong>: Tampilan tabel</li></ul><h3 id="sorting" tabindex="-1">Sorting <a class="header-anchor" href="#sorting" aria-label="Permalink to &quot;Sorting&quot;">​</a></h3><ul><li>Sort berdasarkan nama, tipe, tanggal update</li><li>Default: Sort berdasarkan tanggal update (terbaru dulu)</li></ul><h3 id="pagination" tabindex="-1">Pagination <a class="header-anchor" href="#pagination" aria-label="Permalink to &quot;Pagination&quot;">​</a></h3><ul><li>Gunakan pagination untuk navigasi</li><li>Pilih jumlah item per halaman</li></ul><h2 id="aksi" tabindex="-1">Aksi <a class="header-anchor" href="#aksi" aria-label="Permalink to &quot;Aksi&quot;">​</a></h2><h3 id="lihat-detail-dokumen" tabindex="-1">Lihat Detail Dokumen <a class="header-anchor" href="#lihat-detail-dokumen" aria-label="Permalink to &quot;Lihat Detail Dokumen&quot;">​</a></h3><ol><li>Klik nama dokumen di tabel</li><li>Atau klik tombol &quot;Meta Data ✓&quot; / &quot;Lengkapi Meta Data&quot;</li><li>Anda akan diarahkan ke halaman detail dokumen</li></ol><h3 id="upload-dokumen" tabindex="-1">Upload Dokumen <a class="header-anchor" href="#upload-dokumen" aria-label="Permalink to &quot;Upload Dokumen&quot;">​</a></h3><ol><li>Klik tombol <strong>Upload File</strong> di sidebar atau menu</li><li>Anda akan diarahkan ke halaman upload</li><li><strong>Catatan</strong>: Lihat panduan <a href="./upload-document.html">Upload Dokumen</a></li></ol><h3 id="tambah-folder" tabindex="-1">Tambah Folder <a class="header-anchor" href="#tambah-folder" aria-label="Permalink to &quot;Tambah Folder&quot;">​</a></h3><ol><li>Klik tombol <strong>Add Folder</strong> atau **Add new folder&quot;</li><li>Masukkan nama folder</li><li>Pilih parent folder (opsional)</li><li>Klik <strong>Buat</strong> untuk menyimpan</li></ol><h3 id="rename-folder" tabindex="-1">Rename Folder <a class="header-anchor" href="#rename-folder" aria-label="Permalink to &quot;Rename Folder&quot;">​</a></h3><ol><li>Klik menu (3 dots) di folder</li><li>Pilih <strong>Rename</strong></li><li>Masukkan nama baru</li><li>Klik <strong>Simpan</strong></li></ol><h3 id="delete-folder" tabindex="-1">Delete Folder <a class="header-anchor" href="#delete-folder" aria-label="Permalink to &quot;Delete Folder&quot;">​</a></h3><ol><li>Klik menu (3 dots) di folder</li><li>Pilih <strong>Delete</strong></li><li>Konfirmasi penghapusan</li><li><strong>Catatan</strong>: Hanya administrator yang dapat delete</li></ol><h2 id="storage-information" tabindex="-1">Storage Information <a class="header-anchor" href="#storage-information" aria-label="Permalink to &quot;Storage Information&quot;">​</a></h2><p>Sidebar kanan menampilkan:</p><ul><li><strong>Total Storage</strong>: Total storage yang digunakan</li><li><strong>Storage Breakdown</strong>: Breakdown berdasarkan tipe file</li><li><strong>Progress Bar</strong>: Visualisasi penggunaan storage</li></ul><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;Tips&quot;">​</a></h2><ul><li>Gunakan folder untuk mengorganisir dokumen</li><li>Gunakan search untuk menemukan dokumen dengan cepat</li><li>Lengkapi metadata dokumen untuk memudahkan pencarian</li><li>Gunakan filter untuk melihat dokumen spesifik</li></ul><h2 id="langkah-selanjutnya" tabindex="-1">Langkah Selanjutnya <a class="header-anchor" href="#langkah-selanjutnya" aria-label="Permalink to &quot;Langkah Selanjutnya&quot;">​</a></h2><ul><li><a href="./upload-document.html">Upload Dokumen</a> - Pelajari cara upload dokumen</li><li><a href="./document-detail.html">Detail Dokumen</a> - Pelajari detail dokumen</li><li><a href="./document-folders.html">Folder &amp; Kategori</a> - Pelajari manajemen folder</li></ul>',46)])])}const g=e(t,[["render",r]]);export{k as __pageData,g as default};
