import{_ as i,c as e,o as n,ag as t}from"./chunks/framework.jxTED6Pp.js";const m=JSON.parse('{"title":"Laporan Keuangan - Edit Laporan","description":"","frontmatter":{},"headers":[],"relativePath":"features/edit-report.md","filePath":"features/edit-report.md"}'),l={name:"features/edit-report.md"};function r(o,a,d,s,u,h){return n(),e("div",null,[...a[0]||(a[0]=[t('<h1 id="laporan-keuangan-edit-laporan" tabindex="-1">Laporan Keuangan - Edit Laporan <a class="header-anchor" href="#laporan-keuangan-edit-laporan" aria-label="Permalink to &quot;Laporan Keuangan - Edit Laporan&quot;">​</a></h1><p>Panduan untuk mengedit laporan keuangan yang sudah ada.</p><h2 id="akses-halaman" tabindex="-1">Akses Halaman <a class="header-anchor" href="#akses-halaman" aria-label="Permalink to &quot;Akses Halaman&quot;">​</a></h2><ul><li>Dari halaman daftar laporan, klik tombol <strong>Edit</strong> di kolom Aksi</li><li>Atau dari halaman detail laporan, klik tombol <strong>Edit</strong></li><li>Atau akses langsung: <code>/reports/{id}/edit</code></li></ul><h2 id="form-edit" tabindex="-1">Form Edit <a class="header-anchor" href="#form-edit" aria-label="Permalink to &quot;Form Edit&quot;">​</a></h2><p>Form edit sama dengan form tambah, tetapi sudah terisi dengan data yang ada.</p><h3 id="field-yang-dapat-diedit" tabindex="-1">Field yang Dapat Diedit <a class="header-anchor" href="#field-yang-dapat-diedit" aria-label="Permalink to &quot;Field yang Dapat Diedit&quot;">​</a></h3><ul><li><strong>Periode</strong>: Dapat diubah (jika belum ada duplicate)</li><li><strong>Perusahaan</strong>: Dapat diubah</li><li><strong>Revenue</strong>: Dapat diubah</li><li><strong>OPEX</strong>: Dapat diubah</li><li><strong>NPAT</strong>: Dapat diubah</li><li><strong>Dividend</strong>: Dapat diubah</li><li><strong>Financial Ratio</strong>: Akan terhitung ulang otomatis</li><li><strong>Inputter</strong>: Dapat diubah</li><li><strong>Remark</strong>: Dapat diubah</li><li><strong>Attachment</strong>: Dapat diubah atau dihapus</li></ul><h2 id="validasi" tabindex="-1">Validasi <a class="header-anchor" href="#validasi" aria-label="Permalink to &quot;Validasi&quot;">​</a></h2><p>Sama seperti form tambah:</p><ul><li>Periode harus format YYYY-MM</li><li>Tidak boleh ada duplicate periode untuk perusahaan yang sama</li><li>Financial Ratio akan terhitung ulang otomatis</li></ul><h2 id="menyimpan-perubahan" tabindex="-1">Menyimpan Perubahan <a class="header-anchor" href="#menyimpan-perubahan" aria-label="Permalink to &quot;Menyimpan Perubahan&quot;">​</a></h2><ol><li><p><strong>Review Perubahan</strong></p><ul><li>Pastikan semua perubahan sudah benar</li><li>Cek perhitungan financial ratio</li></ul></li><li><p><strong>Klik &quot;Update&quot;</strong></p><ul><li>Sistem akan menyimpan perubahan</li><li>Jika berhasil, Anda akan diarahkan ke halaman detail laporan</li></ul></li><li><p><strong>Jika Ada Error</strong></p><ul><li>Sistem akan menampilkan pesan error</li><li>Perbaiki field yang error</li><li>Coba simpan lagi</li></ul></li></ol><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;Tips&quot;">​</a></h2><ul><li>Pastikan periode masih valid setelah edit</li><li>Cek perhitungan financial ratio setelah perubahan</li><li>Backup data penting sebelum melakukan perubahan besar</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="tidak-bisa-edit-periode" tabindex="-1">Tidak Bisa Edit Periode <a class="header-anchor" href="#tidak-bisa-edit-periode" aria-label="Permalink to &quot;Tidak Bisa Edit Periode&quot;">​</a></h3><ul><li>Jika periode baru sudah digunakan perusahaan lain, tidak bisa diubah</li><li>Gunakan periode yang berbeda atau hapus laporan lama</li></ul><h3 id="financial-ratio-tidak-update" tabindex="-1">Financial Ratio Tidak Update <a class="header-anchor" href="#financial-ratio-tidak-update" aria-label="Permalink to &quot;Financial Ratio Tidak Update&quot;">​</a></h3><ul><li>Pastikan Revenue dan OPEX sudah diubah</li><li>Sistem akan menghitung ulang otomatis saat simpan</li><li>Refresh halaman jika perhitungan tidak muncul</li></ul><h2 id="langkah-selanjutnya" tabindex="-1">Langkah Selanjutnya <a class="header-anchor" href="#langkah-selanjutnya" aria-label="Permalink to &quot;Langkah Selanjutnya&quot;">​</a></h2><ul><li><a href="./financial-reports.html">Detail Laporan</a> - Lihat perubahan yang sudah disimpan</li><li><a href="./financial-reports.html">Daftar Laporan</a> - Kembali ke daftar laporan</li></ul>',22)])])}const g=i(l,[["render",r]]);export{m as __pageData,g as default};
