import{_ as i,c as n,o as e,ag as l}from"./chunks/framework.jxTED6Pp.js";const m=JSON.parse('{"title":"Manajemen Pengguna - Edit Pengguna","description":"","frontmatter":{},"headers":[],"relativePath":"features/edit-user.md","filePath":"features/edit-user.md"}'),s={name:"features/edit-user.md"};function t(r,a,d,o,u,h){return e(),n("div",null,[...a[0]||(a[0]=[l('<h1 id="manajemen-pengguna-edit-pengguna" tabindex="-1">Manajemen Pengguna - Edit Pengguna <a class="header-anchor" href="#manajemen-pengguna-edit-pengguna" aria-label="Permalink to &quot;Manajemen Pengguna - Edit Pengguna&quot;">​</a></h1><p>Panduan untuk mengedit data pengguna yang sudah ada.</p><h2 id="akses-halaman" tabindex="-1">Akses Halaman <a class="header-anchor" href="#akses-halaman" aria-label="Permalink to &quot;Akses Halaman&quot;">​</a></h2><ul><li>Dari halaman daftar pengguna, klik tombol <strong>Edit</strong> di kolom Aksi</li><li>Atau dari halaman detail pengguna, klik tombol <strong>Edit</strong></li><li>Atau akses langsung: <code>/users/{id}/edit</code></li><li><strong>Catatan</strong>: Hanya admin/administrator yang dapat akses</li></ul><h2 id="form-edit" tabindex="-1">Form Edit <a class="header-anchor" href="#form-edit" aria-label="Permalink to &quot;Form Edit&quot;">​</a></h2><p>Form edit sama dengan form tambah, tetapi sudah terisi dengan data yang ada.</p><h3 id="field-yang-dapat-diedit" tabindex="-1">Field yang Dapat Diedit <a class="header-anchor" href="#field-yang-dapat-diedit" aria-label="Permalink to &quot;Field yang Dapat Diedit&quot;">​</a></h3><ul><li><strong>Username</strong>: Dapat diubah (jika belum digunakan pengguna lain)</li><li><strong>Email</strong>: Dapat diubah (jika belum digunakan pengguna lain)</li><li><strong>Password</strong>: Dapat diubah (kosongkan jika tidak ingin mengubah)</li><li><strong>Role</strong>: Dapat diubah</li><li><strong>Nama Lengkap</strong>: Dapat diubah</li><li><strong>Telepon</strong>: Dapat diubah</li><li><strong>Status</strong>: Dapat diubah (aktif/nonaktif)</li></ul><h3 id="assignment-perusahaan" tabindex="-1">Assignment Perusahaan <a class="header-anchor" href="#assignment-perusahaan" aria-label="Permalink to &quot;Assignment Perusahaan&quot;">​</a></h3><p><strong>Menambah Assignment:</strong></p><ol><li>Klik &quot;Tambah Assignment&quot;</li><li>Pilih perusahaan dan role</li><li>Simpan assignment</li></ol><p><strong>Edit Assignment:</strong></p><ol><li>Klik pada assignment yang ingin diedit</li><li>Ubah perusahaan atau role</li><li>Simpan perubahan</li></ol><p><strong>Menghapus Assignment:</strong></p><ol><li>Klik tombol hapus (X) di assignment</li><li>Konfirmasi penghapusan</li></ol><h2 id="validasi" tabindex="-1">Validasi <a class="header-anchor" href="#validasi" aria-label="Permalink to &quot;Validasi&quot;">​</a></h2><p>Sama seperti form tambah:</p><ul><li>Username harus unik (jika diubah)</li><li>Email harus unik (jika diubah)</li><li>Password harus memenuhi kriteria (jika diubah)</li><li>Company assignment harus valid</li></ul><h2 id="menyimpan-perubahan" tabindex="-1">Menyimpan Perubahan <a class="header-anchor" href="#menyimpan-perubahan" aria-label="Permalink to &quot;Menyimpan Perubahan&quot;">​</a></h2><ol><li><p><strong>Review Perubahan</strong></p><ul><li>Pastikan semua perubahan sudah benar</li><li>Cek assignment perusahaan</li></ul></li><li><p><strong>Klik &quot;Update&quot;</strong></p><ul><li>Sistem akan menyimpan perubahan</li><li>Jika berhasil, Anda akan diarahkan ke halaman detail pengguna</li></ul></li><li><p><strong>Jika Ada Error</strong></p><ul><li>Sistem akan menampilkan pesan error</li><li>Perbaiki field yang error</li><li>Coba simpan lagi</li></ul></li></ol><h2 id="reset-password" tabindex="-1">Reset Password <a class="header-anchor" href="#reset-password" aria-label="Permalink to &quot;Reset Password&quot;">​</a></h2><p>Untuk reset password pengguna:</p><ol><li><p><strong>Kosongkan Field Password</strong></p><ul><li>Kosongkan field password di form edit</li><li>Password tidak akan diubah</li></ul></li><li><p><strong>Atau Isi Password Baru</strong></p><ul><li>Isi password baru di field password</li><li>Password akan diubah ke password baru</li></ul></li></ol><p><strong>Catatan</strong>:</p><ul><li>Password tidak ditampilkan di form (untuk keamanan)</li><li>Kosongkan field jika tidak ingin mengubah password</li><li>Isi password baru jika ingin mengubah</li></ul><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;Tips&quot;">​</a></h2><ul><li>Pastikan username dan email masih valid setelah edit</li><li>Update assignment jika pengguna pindah perusahaan</li><li>Nonaktifkan pengguna yang tidak lagi aktif (jangan hapus)</li><li>Backup data penting sebelum melakukan perubahan besar</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="tidak-bisa-edit-username" tabindex="-1">Tidak Bisa Edit Username <a class="header-anchor" href="#tidak-bisa-edit-username" aria-label="Permalink to &quot;Tidak Bisa Edit Username&quot;">​</a></h3><ul><li>Username yang sudah digunakan pengguna lain tidak bisa diubah</li><li>Jika perlu mengubah, hubungi administrator</li></ul><h3 id="tidak-bisa-edit-email" tabindex="-1">Tidak Bisa Edit Email <a class="header-anchor" href="#tidak-bisa-edit-email" aria-label="Permalink to &quot;Tidak Bisa Edit Email&quot;">​</a></h3><ul><li>Email yang sudah digunakan pengguna lain tidak bisa diubah</li><li>Jika perlu mengubah, hubungi administrator</li></ul><h3 id="password-tidak-berubah" tabindex="-1">Password Tidak Berubah <a class="header-anchor" href="#password-tidak-berubah" aria-label="Permalink to &quot;Password Tidak Berubah&quot;">​</a></h3><ul><li>Pastikan field password sudah diisi dengan password baru</li><li>Kosongkan field jika tidak ingin mengubah password</li><li>Pastikan password memenuhi kriteria</li></ul><h2 id="langkah-selanjutnya" tabindex="-1">Langkah Selanjutnya <a class="header-anchor" href="#langkah-selanjutnya" aria-label="Permalink to &quot;Langkah Selanjutnya&quot;">​</a></h2><ul><li><a href="./user-management.html">Daftar Pengguna</a> - Kembali ke daftar pengguna</li></ul>',36)])])}const p=i(s,[["render",t]]);export{m as __pageData,p as default};
