import{_ as l,c as i,o as n,ag as t}from"./chunks/framework.jxTED6Pp.js";const k=JSON.parse('{"title":"Laporan Keuangan - Daftar Laporan","description":"","frontmatter":{},"headers":[],"relativePath":"features/financial-reports.md","filePath":"features/financial-reports.md"}'),r={name:"features/financial-reports.md"};function e(o,a,s,u,h,d){return n(),i("div",null,[...a[0]||(a[0]=[t('<h1 id="laporan-keuangan-daftar-laporan" tabindex="-1">Laporan Keuangan - Daftar Laporan <a class="header-anchor" href="#laporan-keuangan-daftar-laporan" aria-label="Permalink to &quot;Laporan Keuangan - Daftar Laporan&quot;">​</a></h1><p>Halaman ini menampilkan semua laporan keuangan yang dapat Anda akses.</p><h2 id="akses-halaman" tabindex="-1">Akses Halaman <a class="header-anchor" href="#akses-halaman" aria-label="Permalink to &quot;Akses Halaman&quot;">​</a></h2><ul><li>Klik menu <strong>Laporan</strong> di navigasi atas</li><li>Atau akses langsung: <code>/reports</code></li></ul><h2 id="tampilan" tabindex="-1">Tampilan <a class="header-anchor" href="#tampilan" aria-label="Permalink to &quot;Tampilan&quot;">​</a></h2><p>Halaman menampilkan tabel dengan kolom:</p><ul><li><strong>Periode</strong>: Periode laporan (format: YYYY-MM)</li><li><strong>Perusahaan</strong>: Nama perusahaan</li><li><strong>Revenue</strong>: Total pendapatan</li><li><strong>OPEX</strong>: Total biaya operasional</li><li><strong>NPAT</strong>: Net Profit After Tax</li><li><strong>Dividend</strong>: Dividen</li><li><strong>Financial Ratio</strong>: Rasio keuangan</li><li><strong>Status</strong>: Status laporan (RKAP/Realisasi)</li><li><strong>Aksi</strong>: Tombol untuk melihat detail atau edit</li></ul><h2 id="fitur" tabindex="-1">Fitur <a class="header-anchor" href="#fitur" aria-label="Permalink to &quot;Fitur&quot;">​</a></h2><h3 id="pencarian" tabindex="-1">Pencarian <a class="header-anchor" href="#pencarian" aria-label="Permalink to &quot;Pencarian&quot;">​</a></h3><ul><li>Gunakan search box untuk mencari laporan</li><li>Pencarian berdasarkan nama perusahaan atau periode</li></ul><h3 id="filter" tabindex="-1">Filter <a class="header-anchor" href="#filter" aria-label="Permalink to &quot;Filter&quot;">​</a></h3><ul><li><strong>Filter Periode</strong>: Pilih periode tertentu</li><li><strong>Filter Perusahaan</strong>: Pilih perusahaan tertentu</li><li><strong>Filter Status</strong>: RKAP atau Realisasi</li></ul><h3 id="sorting" tabindex="-1">Sorting <a class="header-anchor" href="#sorting" aria-label="Permalink to &quot;Sorting&quot;">​</a></h3><ul><li>Klik header kolom untuk sorting</li><li>Sorting berdasarkan periode, revenue, dll</li></ul><h3 id="pagination" tabindex="-1">Pagination <a class="header-anchor" href="#pagination" aria-label="Permalink to &quot;Pagination&quot;">​</a></h3><ul><li>Gunakan pagination untuk navigasi</li><li>Pilih jumlah item per halaman</li></ul><h2 id="aksi" tabindex="-1">Aksi <a class="header-anchor" href="#aksi" aria-label="Permalink to &quot;Aksi&quot;">​</a></h2><h3 id="lihat-detail" tabindex="-1">Lihat Detail <a class="header-anchor" href="#lihat-detail" aria-label="Permalink to &quot;Lihat Detail&quot;">​</a></h3><ol><li>Klik nama perusahaan atau periode di tabel</li><li>Atau klik tombol <strong>Detail</strong> di kolom Aksi</li><li>Anda akan diarahkan ke halaman detail laporan</li></ol><h3 id="edit-laporan" tabindex="-1">Edit Laporan <a class="header-anchor" href="#edit-laporan" aria-label="Permalink to &quot;Edit Laporan&quot;">​</a></h3><ol><li>Klik tombol <strong>Edit</strong> di kolom Aksi</li><li>Anda akan diarahkan ke halaman edit</li><li><strong>Catatan</strong>: Hanya dapat edit laporan perusahaan yang di-assign</li></ol><h3 id="tambah-laporan-baru" tabindex="-1">Tambah Laporan Baru <a class="header-anchor" href="#tambah-laporan-baru" aria-label="Permalink to &quot;Tambah Laporan Baru&quot;">​</a></h3><ol><li>Klik tombol <strong>Tambah Laporan</strong> di atas tabel</li><li>Anda akan diarahkan ke halaman form tambah laporan</li><li><strong>Catatan</strong>: Lihat panduan <a href="./add-report.html">Tambah Laporan</a></li></ol><h3 id="bulk-upload" tabindex="-1">Bulk Upload <a class="header-anchor" href="#bulk-upload" aria-label="Permalink to &quot;Bulk Upload&quot;">​</a></h3><ol><li>Klik tombol <strong>Bulk Upload</strong> di atas tabel</li><li>Download template Excel</li><li>Isi template dengan data laporan</li><li>Upload file Excel</li><li><strong>Catatan</strong>: Lihat panduan <a href="./bulk-upload-reports.html">Bulk Upload</a></li></ol><h2 id="status-laporan" tabindex="-1">Status Laporan <a class="header-anchor" href="#status-laporan" aria-label="Permalink to &quot;Status Laporan&quot;">​</a></h2><p>Laporan memiliki dua status:</p><ul><li><strong>RKAP</strong>: Rencana Kerja dan Anggaran Perusahaan (planning)</li><li><strong>Realisasi</strong>: Realisasi aktual (actual)</li></ul><p>Sistem mendukung perbandingan RKAP vs Realisasi untuk analisis.</p><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;Tips&quot;">​</a></h2><ul><li>Gunakan filter untuk melihat laporan spesifik</li><li>Gunakan bulk upload untuk input banyak laporan sekaligus</li><li>Export data untuk analisis lebih lanjut</li></ul><h2 id="langkah-selanjutnya" tabindex="-1">Langkah Selanjutnya <a class="header-anchor" href="#langkah-selanjutnya" aria-label="Permalink to &quot;Langkah Selanjutnya&quot;">​</a></h2><ul><li><a href="./add-report.html">Tambah Laporan</a> - Pelajari cara menambah laporan baru</li><li><a href="./edit-report.html">Edit Laporan</a> - Pelajari cara edit laporan</li><li><a href="./bulk-upload-reports.html">Bulk Upload</a> - Pelajari bulk upload via Excel</li></ul>',33)])])}const m=l(r,[["render",e]]);export{k as __pageData,m as default};
