import{_ as i,c as t,o as n,ag as e}from"./chunks/framework.jxTED6Pp.js";const h=JSON.parse('{"title":"Notifikasi - Mark as Read","description":"","frontmatter":{},"headers":[],"relativePath":"features/mark-notifications-read.md","filePath":"features/mark-notifications-read.md"}'),l={name:"features/mark-notifications-read.md"};function o(r,a,s,u,d,k){return n(),t("div",null,[...a[0]||(a[0]=[e('<h1 id="notifikasi-mark-as-read" tabindex="-1">Notifikasi - Mark as Read <a class="header-anchor" href="#notifikasi-mark-as-read" aria-label="Permalink to &quot;Notifikasi - Mark as Read&quot;">​</a></h1><p>Panduan untuk menandai notifikasi sebagai sudah ditindak lanjuti. Setelah ditandai, notifikasi akan otomatis hilang dari daftar.</p><h2 id="mark-single-notifikasi" tabindex="-1">Mark Single Notifikasi <a class="header-anchor" href="#mark-single-notifikasi" aria-label="Permalink to &quot;Mark Single Notifikasi&quot;">​</a></h2><h3 id="cara-tombol-di-notifikasi" tabindex="-1">Cara: Tombol di Notifikasi <a class="header-anchor" href="#cara-tombol-di-notifikasi" aria-label="Permalink to &quot;Cara: Tombol di Notifikasi&quot;">​</a></h3><ol><li><p><strong>Buka Halaman Notifikasi</strong></p><ul><li>Klik icon notifikasi di header</li><li>Atau akses: <code>/notifications</code></li></ul></li><li><p><strong>Klik Tombol &quot;Tandai sudah ditindak lanjuti&quot;</strong></p><ul><li>Tombol berada di setiap notifikasi</li><li>Klik tombol untuk notifikasi yang ingin ditandai</li></ul></li><li><p><strong>Konfirmasi</strong></p><ul><li>Sistem akan menampilkan dialog konfirmasi</li><li>Klik <strong>OK</strong> untuk mengonfirmasi</li><li>Notifikasi akan ditandai sebagai sudah ditindak lanjuti</li><li><strong>Notifikasi akan otomatis hilang dari daftar</strong></li></ul></li></ol><h3 id="peringatan-untuk-dokumen-expired" tabindex="-1">Peringatan untuk Dokumen Expired <a class="header-anchor" href="#peringatan-untuk-dokumen-expired" aria-label="Permalink to &quot;Peringatan untuk Dokumen Expired&quot;">​</a></h3><p>Jika Anda menandai notifikasi dokumen expired sebagai &quot;sudah ditindak lanjuti&quot; tetapi <strong>tidak mengupdate tanggal expired</strong> dokumen tersebut, sistem akan menampilkan peringatan:</p><p><strong>Peringatan:</strong></p><blockquote><p>&quot;Dokumen akan berpotensi terlewat masa aktifnya jika tidak segera diperbarui&quot;</p></blockquote><p><strong>Rekomendasi:</strong></p><ul><li>Sebelum menandai notifikasi dokumen expired sebagai &quot;sudah ditindak lanjuti&quot;, pastikan untuk: <ol><li>Update tanggal expired dokumen di halaman detail dokumen</li><li>Atau perpanjang dokumen tersebut</li><li>Baru kemudian tandai notifikasi sebagai &quot;sudah ditindak lanjuti&quot;</li></ol></li></ul><h2 id="mark-all-notifications" tabindex="-1">Mark All Notifications <a class="header-anchor" href="#mark-all-notifications" aria-label="Permalink to &quot;Mark All Notifications&quot;">​</a></h2><p><strong>Catatan Penting:</strong></p><ul><li>Tombol &quot;Hapus Semua&quot; hanya tersedia untuk role <strong>Administrator</strong></li><li>User reguler dan admin tidak akan melihat tombol ini</li></ul><p>Untuk Administrator yang ingin menghapus semua notifikasi:</p><ol><li><p><strong>Buka Halaman Notifikasi</strong></p><ul><li>Klik icon notifikasi di header</li><li>Atau akses: <code>/notifications</code></li></ul></li><li><p><strong>Klik Tombol &quot;Hapus Semua&quot;</strong></p><ul><li>Tombol berada di atas daftar notifikasi (hanya untuk Administrator)</li><li>Klik untuk menghapus semua notifikasi</li></ul></li><li><p><strong>Konfirmasi</strong></p><ul><li>Semua notifikasi akan dihapus</li><li>Unread count di header akan menjadi 0</li></ul></li></ol><h2 id="perilaku-setelah-mark-as-read" tabindex="-1">Perilaku Setelah Mark as Read <a class="header-anchor" href="#perilaku-setelah-mark-as-read" aria-label="Permalink to &quot;Perilaku Setelah Mark as Read&quot;">​</a></h2><p>Setelah notifikasi ditandai sebagai &quot;sudah ditindak lanjuti&quot;:</p><ul><li>✅ Notifikasi akan <strong>otomatis hilang dari daftar</strong> di halaman notifikasi</li><li>✅ Push notification untuk notifikasi tersebut <strong>tidak akan muncul lagi</strong></li><li>✅ Unread count di header akan berkurang</li><li>✅ Notifikasi tidak akan muncul lagi di polling berikutnya</li></ul><p><strong>Catatan:</strong></p><ul><li>Notifikasi yang sudah ditindak lanjuti tidak dapat dilihat lagi di halaman notifikasi</li><li>Jika Anda perlu melihat riwayat notifikasi, hubungi administrator</li></ul><h2 id="unread-count" tabindex="-1">Unread Count <a class="header-anchor" href="#unread-count" aria-label="Permalink to &quot;Unread Count&quot;">​</a></h2><ul><li><strong>Icon Notifikasi</strong>: Menampilkan jumlah notifikasi yang belum ditindak lanjuti</li><li><strong>Update Otomatis</strong>: Jumlah akan update setelah mark as read</li><li><strong>Administrator/Superadmin</strong>: Melihat total notifikasi semua pengguna</li><li><strong>Admin</strong>: Melihat notifikasi dari perusahaan mereka dan perusahaan anak</li><li><strong>User Reguler</strong>: Hanya melihat notifikasi mereka sendiri</li></ul><h2 id="tips" tabindex="-1">Tips <a class="header-anchor" href="#tips" aria-label="Permalink to &quot;Tips&quot;">​</a></h2><ul><li><strong>Tindak Lanjuti Segera</strong>: Segera tindak lanjuti notifikasi dokumen expired untuk menghindari dokumen terlewat masa aktifnya</li><li><strong>Update Dokumen</strong>: Sebelum menandai notifikasi dokumen expired, pastikan untuk mengupdate tanggal expired dokumen tersebut</li><li><strong>Cek Push Notification</strong>: Perhatikan push notification yang muncul otomatis setiap 2 menit</li><li><strong>Cek Secara Berkala</strong>: Cek notifikasi secara berkala untuk tidak melewatkan informasi penting</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="tombol-tidak-berfungsi" tabindex="-1">Tombol Tidak Berfungsi <a class="header-anchor" href="#tombol-tidak-berfungsi" aria-label="Permalink to &quot;Tombol Tidak Berfungsi&quot;">​</a></h3><ul><li>Refresh halaman dan coba lagi</li><li>Cek koneksi internet</li><li>Pastikan Anda memiliki akses untuk menandai notifikasi tersebut</li><li>Hubungi administrator jika masalah berlanjut</li></ul><h3 id="unread-count-tidak-update" tabindex="-1">Unread Count Tidak Update <a class="header-anchor" href="#unread-count-tidak-update" aria-label="Permalink to &quot;Unread Count Tidak Update&quot;">​</a></h3><ul><li>Refresh halaman setelah mark as read</li><li>Cek apakah notifikasi benar-benar sudah di-mark as read</li><li>Unread count akan update otomatis setelah beberapa detik</li><li>Clear cache browser jika perlu</li></ul><h3 id="notifikasi-masih-muncul-setelah-mark-as-read" tabindex="-1">Notifikasi Masih Muncul Setelah Mark as Read <a class="header-anchor" href="#notifikasi-masih-muncul-setelah-mark-as-read" aria-label="Permalink to &quot;Notifikasi Masih Muncul Setelah Mark as Read&quot;">​</a></h3><ul><li>Notifikasi seharusnya otomatis hilang dari daftar</li><li>Refresh halaman jika notifikasi masih muncul</li><li>Pastikan konfirmasi mark as read berhasil</li><li>Hubungi administrator jika masalah berlanjut</li></ul><h2 id="langkah-selanjutnya" tabindex="-1">Langkah Selanjutnya <a class="header-anchor" href="#langkah-selanjutnya" aria-label="Permalink to &quot;Langkah Selanjutnya&quot;">​</a></h2><ul><li><a href="./notifications.html">Daftar Notifikasi</a> - Kembali ke daftar notifikasi</li><li><a href="./settings.html">Settings</a> - Pelajari pengaturan notifikasi</li></ul>',34)])])}const g=i(l,[["render",o]]);export{h as __pageData,g as default};
